mapper.select:
SELECT
	f.taskfileurl
FROM
	t_taskfile f
JOIN t_task t ON (
	f.taskfileid = #{taskfileid}
	AND f.taskid = t.taskid
	AND t.creator_userid = #{creator_userid}
)

SELECT f.taskfileid, f.creator_userid, f.operator_userid, f.taskfilename, f.taskfileurl, f.operatime FROM t_taskfile f WHERE f.taskid = #{detail.taskid} AND f.taskfiletype = #{detail.taskfiletype}
AND f.creator_userid = #{detail.cUser.userid}
AND f.operator_userid = #{detail.cUser.userid}

SELECT COUNT(1) FROM
t_taskfile WHERE taskid =
#{taskid}

mapper.delete:
DELETE FROM t_taskfile WHERE taskfileid =
#{taskfileid}
AND taskfiletype = #{taskfiletype}
