mapper.select:
SELECT
	f.taskfileurl
FROM
	t_taskfile f
JOIN t_task t ON (
	f.taskfileid = #{taskfileid}
	AND f.taskid = t.taskid
	AND t.creator_userid = #{creator_userid}
)

SELECT COUNT(1) FROM
t_taskfile WHERE taskid =
#{taskid}

mapper.delete:
DELETE FROM t_taskfile WHERE taskfileid =
#{taskfileid}
AND taskfiletype = #{taskfiletype}
